import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import styled from "styled-components";

const ImgSlider = () => {
  // setup img slider slick-carousel
  let settings = {
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };
  return (
    <div>
      <Carousel {...settings}>
        <div>
          <h2>1</h2>
        </div>
        <div>
          <h2>2</h2>
        </div>
        <div>
          <h2>3</h2>
        </div>
      </Carousel>
    </div>
  );
};

// Konfugasi style slider utk carousel
const Carousel = styled(Slider)`
  margin-top: 20px;

  /* setup button next & previous */
  & > button {
    opacity: 0;
    height: 100%;
    width: 5vw;
    z-index: 1;

    &:hover {
      opacity: 1;
      transition: opacity 0.2s ease 0s;
    }
  }

  /* setup bullets button yg dibawah */
  ul li button {
    &::before {
      font-size: 20px;
      color: rgb(150, 158, 171);
    }
  }
  /* Kondisi bullet saat aktif */
  li.slick-active button::before {
    color: white;
  }

  /* ini berguna utk liat gambar selanjutnya
  yg artinya akan ada 3 gambar yaitu sebagian 
  gambar sblmnya, 1 full gambar active dan 
  sebagian gambar selanjutnya */
  .slick-list {
    overflow: initial;
  }

  .slick-prev {
    left: -75px;
  }
  .slick-next {
    right: -75px;
  }
`;

const Wrap = styled.div``;

export default ImgSlider;
